using LNBolt;
using LNDroneController.LND;
using NUnit.Framework;
using ServiceStack;
using ServiceStack.Text;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace LNDroneController.Tests
{
    public class OnionTests
    {
        [Test]
        public void ContructOnion()
        {
            var firstPubliKey = "02eec7245d6b7d2ccb30380bfbe2a3648cd7a942653f5aa340edcea1f283686619".HexToBytes();
            var associatedData = "4242424242424242424242424242424242424242424242424242424242424242".HexToBytes();
            var sharedSecrets = new List<byte[]>
            {
                "53eb63ea8a3fec3b3cd433b85cd62a4b145e1dda09391b348c4e1cd36a03ea66".HexToBytes(),
                "a6519e98832a0b179f62123b3567c106db99ee37bef036e783263602f3488fae".HexToBytes(),
                "3a6b412548762f0dbccce5c7ae7bb8147d1caf9b5471c34120b30bc9c04891cc".HexToBytes(),
                "21e13c2d7cfe7e18836df50872466117a295783ab8aab0e7ecc8c725503ad02d".HexToBytes(),
                "b5756b9b542727dbafc6765a49488b023a725d631af688fc031217e90770c328".HexToBytes(),
            };
            var payloads = new List<HopPayload>();
            for(int i = 0; i < 5; i++)
            {
                var currentChannelId = Enumerable.Repeat((byte)i , 8).ToArray();
                var currentPayload = new HopPayload
                {
                    ChannelId = currentChannelId,
                    AmountToForward = (ulong)i,
                    OutgoingCltvValue = (uint)i,
                };
                payloads.Add(currentPayload);
            }
            var onion = OnionBlob.ConstructOnion(sharedSecrets, payloads, firstPubliKey, associatedData);
            var readOnion = new OnionBlob(onion.RawOnion);
            Assert.AreEqual(onion.RawOnion, readOnion.RawOnion);
            Assert.AreEqual(onion.RawOnion.Length, 1366);
            Assert.AreEqual(onion.RawOnion.ToHex(), "");

        }

        [Test]
        public void PeelOnion()
        {
            var onion = Convert.FromHexString("");
            var decoder = new OnionBlob(onion);
            var associatedData = Convert.FromHexString("4242424242424242424242424242424242424242424242424242424242424242");
            var hopPrivateKeys = new List<byte[]> {
                Convert.FromHexString("4141414141414141414141414141414141414141414141414141414141414141"),
                Convert.FromHexString("4242424242424242424242424242424242424242424242424242424242424242"),
                Convert.FromHexString("4343434343434343434343434343434343434343434343434343434343434343"),
                Convert.FromHexString("4444444444444444444444444444444444444444444444444444444444444444"),
                Convert.FromHexString("4545454545454545454545454545454545454545454545454545454545454545")
            };
            var peel0 = decoder.Peel(hopPrivateKey: hopPrivateKeys[0], associatedData: associatedData);
            var peel1 = decoder.Peel(hopPrivateKey: hopPrivateKeys[1], associatedData: associatedData);
            var peel2 = decoder.Peel(hopPrivateKey: Convert.FromHexString("3a6b412548762f0dbccce5c7ae7bb8147d1caf9b5471c34120b30bc9c04891cc"), associatedData: associatedData);
            var peel3 = decoder.Peel(hopPrivateKey: hopPrivateKeys[3], associatedData: associatedData);
            var peel4 = decoder.Peel(hopPrivateKey: hopPrivateKeys[4], associatedData: associatedData);
            Assert.That(peel4, Is.Null);
            var lastHopPayload = peel4.hopPayload;
            Assert.That(lastHopPayload.OutgoingCltvValue == 4);
        }

